def calculate_matrix(birth_date_str: str) -> dict:
    try:
        day, month, year = map(int, birth_date_str.split('.'))
        point_a = reduce_to_22(day)
        point_b = reduce_to_22(month)
        point_v = reduce_to_22(sum(int(d) for d in str(year)))
        point_g = reduce_to_22(point_a + point_b + point_v)
        point_d = reduce_to_22(point_a + point_b + point_v + point_g)
        return {"A": point_a, "B": point_b, "V": point_v, "G": point_g, "D": point_d}
    except Exception:
        return None

def get_full_report(birth_date_str: str) -> str:
    matrix = calculate_matrix(birth_date_str)
    if not matrix:
        return "–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø—Ä–æ–≤–µ—Ä—å—Ç–µ —Ñ–æ—Ä–º–∞—Ç –¥–∞—Ç—ã. –ù—É–∂–Ω–æ –≤–≤–µ—Å—Ç–∏ –î–î.–ú–ú.–ì–ì–ì–ì, —á—Ç–æ–±—ã —Ä–∞—Å—á–µ—Ç –±—ã–ª —Ç–æ—á–Ω—ã–º. ‚ú®"
    
    report = f"‚ú® **–í–∞—à –ª–∏—á–Ω—ã–π —Ä–∞–∑–±–æ—Ä –æ—Ç –î–∏–∞–Ω—ã** ‚ú®\n\n"
    report += f"–Ø –ø—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä–æ–≤–∞–ª–∞ –≤–∞—à—É –¥–∞—Ç—É —Ä–æ–∂–¥–µ–Ω–∏—è ({birth_date_str}). –í –Ω–µ–π –∑–∞–ª–æ–∂–µ–Ω –æ–≥—Ä–æ–º–Ω—ã–π –ø–æ—Ç–µ–Ω—Ü–∏–∞–ª! –î–∞–≤–∞–π—Ç–µ —Ä–∞–∑–±–µ—Ä–µ–º –∫–ª—é—á–µ–≤—ã–µ —ç–Ω–µ—Ä–≥–∏–∏ –≤–∞—à–µ–π –º–∞—Ç—Ä–∏—Ü—ã:\n\n"
    
    report += f"üåü **–í–∞—à–∞ –í–∏–∑–∏—Ç–Ω–∞—è –ö–∞—Ä—Ç–æ—á–∫–∞ ({matrix['A']} –∞—Ä–∫–∞–Ω)**\n"
    report += f"–≠—Ç–æ —Ç–æ, –∫–∞–∫ –≤–∞—Å –≤–æ—Å–ø—Ä–∏–Ω–∏–º–∞–µ—Ç –º–∏—Ä, –≤–∞—à –æ—Å–Ω–æ–≤–Ω–æ–π –ø–æ—Ç–µ–Ω—Ü–∏–∞–ª. {get_arcane_description(matrix['A'])}\n\n"
    
    report += f"üå™ **–í–∞—à –ö–∞—Ä–º–∏—á–µ—Å–∫–∏–π –•–≤–æ—Å—Ç ({matrix['G']} –∞—Ä–∫–∞–Ω)**\n"
    report += "–≠—Ç–æ –∑–∞–¥–∞—á–∏, –∫–æ—Ç–æ—Ä—ã–µ —Ç—Ä–µ–±—É—é—Ç –æ—Å–æ–∑–Ω–∞–Ω–∏—è. –ö–∞–∫ —Ç–æ–ª—å–∫–æ –≤—ã –∏—Ö –ø—Ä–æ—Ä–∞–±–∞—Ç—ã–≤–∞–µ—Ç–µ, –æ–Ω–∏ —Å—Ç–∞–Ω–æ–≤—è—Ç—Å—è –≤–∞—à–∏–º –º–æ—â–Ω—ã–º —Ä–µ—Å—É—Ä—Å–æ–º. "
    report += f"{get_arcane_description(matrix['G'])}\n\n"
    
    report += f"üíé **–í–∞—à –¶–µ–Ω—Ç—Ä –°–∏–ª—ã ({matrix['D']} –∞—Ä–∫–∞–Ω)**\n"
    report += "–≠—Ç–æ —Ç–æ—á–∫–∞ –≤–∞—à–µ–≥–æ –≤–Ω—É—Ç—Ä–µ–Ω–Ω–µ–≥–æ –±–∞–ª–∞–Ω—Å–∞. –ö–æ–≥–¥–∞ –≤—ã –∂–∏–≤–µ—Ç–µ –≤ —Å–æ–≥–ª–∞—Å–∏–∏ —Å —ç—Ç–æ–π —ç–Ω–µ—Ä–≥–∏–µ–π, –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–∏ –∏ —É—Å–ø–µ—Ö –ø—Ä–∏—Ö–æ–¥—è—Ç –∫ –≤–∞–º –≥–æ—Ä–∞–∑–¥–æ –ª–µ–≥—á–µ. "
    report += f"{get_arcane_description(matrix['D'])}\n\n"
    
    report += "--- \n"
    report += "–≠—Ç–æ –ª–∏—à—å –Ω–∞—á–∞–ª–æ –ø—É—Ç–∏ –∫ –ø–æ–Ω–∏–º–∞–Ω–∏—é —Å–µ–±—è. –ö–∞–∂–¥–∞—è —Ü–∏—Ñ—Ä–∞ –≤ –º–∞—Ç—Ä–∏—Ü–µ ‚Äî —ç—Ç–æ –≤–∞–∂–Ω–∞—è –ø–æ–¥—Å–∫–∞–∑–∫–∞. –ï—Å–ª–∏ –≤—ã —á—É–≤—Å—Ç–≤—É–µ—Ç–µ, —á—Ç–æ –≥–æ—Ç–æ–≤—ã —Ä–∞–∑–æ–±—Ä–∞—Ç—å —Å–≤–æ—é —Å–∏—Ç—É–∞—Ü–∏—é –≥–ª—É–±–∂–µ (—Ñ–∏–Ω–∞–Ω—Å—ã, –æ—Ç–Ω–æ—à–µ–Ω–∏—è, –ø—Ä–µ–¥–Ω–∞–∑–Ω–∞—á–µ–Ω–∏–µ) ‚Äî —è –≤—Å–µ–≥–¥–∞ –≥–æ—Ç–æ–≤–∞ –ø–æ–º–æ—á—å. ‚ù§Ô∏è"
    
    return report
